DECLARE @dbCreator			VARCHAR(60);	-- User ID of responsible user 
DECLARE @WorkstationTime	DATETIME;		-- currently not used

BEGIN TRY
	BEGIN TRANSACTION

	SELECT @dbCreator = 'hsta';			
	SELECT @WorkstationTime = getdate();

	-- t_EHR_Area	
	WITH EHR_Area (AreaKey, Ordinal, AreaName, AreaDescription, AreaAbbreviation, EHROnly, CreateBy)
	AS (
		SELECT	*
		FROM(VALUES
			 (1,		1,	'Patient Summary',			'Patient Summary',					'PT Smry',	1, @dbCreator)
			,(2,		2,	'Patient Communications',	'Patient Communications',			'PT Comm',	1, @dbCreator)
			,(12,		3,	'Pre-op Communications',	'Pre-op Patient Communications',	'Pre Com',	1, @dbCreator)
			,(11,		4,	'Registration',				'Registration',						'Reg',		0, @dbCreator)
			,(3,		5,	'Pre-Op',					'Pre-Op',							'Pre-Op',	0, @dbCreator)
			,(4,		6,	'Intra-Op',					'Intra-Op',							'Intra-Op',	0, @dbCreator)
			,(5,		7,	'Anesthesia',				'Anesthesia',						'ANES',		0, @dbCreator)
			,(6,		8,	'PACU',						'Post Anesthesia Care Unit',		'PACU',		0, @dbCreator)
			,(7,		9,	'Post-Op',					'Post-Op',							'Post-Op',	0, @dbCreator)
			,(10,		10,	'Discharge',				'Discharge',						'DSCHRG',	0, @dbCreator)
			,(8,		11,	'Physician',				'Physician',						'MD',		1, @dbCreator)
			,(9,		12,	'23-Hour Stay',				'23-Hour Stay',						'23 Hour',	0, @dbCreator)
			,(13,		13,	'Post-op Communications',	'Post-op Patient Communications',	'Post Com',	1, @dbCreator)
			)
		AS	temp (AreaKey, Ordinal, AreaName, AreaDescription, AreaAbbreviation, EHROnly, CreateBy)
	)
	MERGE INTO	t_EHR_Area AS Target
	USING		EHR_Area AS Source
	ON			Target.AreaKey = Source.AreaKey
	WHEN		MATCHED AND (Target.Ordinal <> Source.Ordinal OR Target.AreaName <> Source.AreaName OR Target.AreaDescription <> Source.AreaDescription OR Target.AreaAbbreviation <> Source.AreaAbbreviation OR Target.EHROnly <> Source.EHROnly) THEN
	UPDATE		SET Target.Ordinal = Source.Ordinal
				,Target.AreaName = Source.AreaName
				,Target.AreaDescription = Source.AreaDescription
				,Target.AreaAbbreviation = Source.AreaAbbreviation
				,Target.EHROnly = Source.EHROnly
	WHEN		NOT MATCHED BY TARGET THEN
	INSERT		(AreaKey, Ordinal, AreaName, AreaDescription, AreaAbbreviation, EHROnly, CreateBy) 
	VALUES		(Source.AreaKey, Source.Ordinal, Source.AreaName, Source.AreaDescription, Source.AreaAbbreviation, Source.EHROnly, Source.CreateBy);


