	WITH EHR_UnitOfMeasure (UnitOfMeasure, UnitOfMeasureDescription, [Status], CreateBy)
	AS 
	(
		SELECT	*
		FROM(VALUES
			  ('d', 'day', 'A', @dbCreator)
			 ,('g', 'gram', 'A', @dbCreator)
			 ,('kg', 'kilogram', 'A', @dbCreator)
			 ,('L', 'liter', 'A', @dbCreator)
			 ,('mcg', 'microgram', 'A', @dbCreator)
			 ,('uL', 'microliter', 'A', @dbCreator)
			 ,('meq', 'milliequivalent', 'A', @dbCreator)
			 ,('mg', 'milligram', 'A', @dbCreator)
			 ,('mL', 'milliliter', 'A', @dbCreator)
			 ,('mm', 'millimeter', 'A', @dbCreator)
			 ,('U', 'unit - catalytic activity', 'A', @dbCreator)			 
			 ,('gtt(s)', 'drop(s)', 'A', @dbCreator)
			)
		AS	temp (UnitOfMeasure, UnitOfMeasureDescription, [Status], CreateBy)
	)
	MERGE INTO	t_EHR_UnitOfMeasure AS Target
	USING		EHR_UnitOfMeasure AS Source
	ON			Target.UnitOfMeasure = Source.UnitOfMeasure
	WHEN		MATCHED AND (Target.UnitOfMeasureDescription <> Source.UnitOfMeasureDescription OR Target.[Status] <> Source.[Status]) THEN
	UPDATE		SET Target.UnitOfMeasureDescription = Source.UnitOfMeasureDescription, Target.[Status] = Source.[Status], Target.ChangeBy = @dbCreator, Target.ChangeDate = GETDATE()
	WHEN		NOT MATCHED BY TARGET THEN
	INSERT		(UnitOfMeasure, UnitOfMeasureDescription, [Status], CreateBy) 
	VALUES		(Source.UnitOfMeasure, Source.UnitOfMeasureDescription, Source.[Status], Source.CreateBy);

