   -- t_EHR_ConsentType -- Module105
   -- facility consent.  only patient signs
   -- procedure consent - all three must sign
   -- acknowledgement - patient and witness must sign

	WITH ConsentType (CenterID, ID, Name,				[Description],																				[Signatures],	[Validations],	[Readonly],	[Status],	CreateBy)
		AS (
			SELECT	*
			FROM(VALUES
				( @centerID,	1, 'Facility',			'Facility Consent (Signed by Patient)',														4,				4,				0,			'A',		@dbCreator)
				,( @centerID,	2, 'Procedure',			'(A) Procedure Consent (Signed by Patient, Witness and Physician)',							7,				7,				1,			'A',		@dbCreator)
				,( @centerID,	3, 'Acknowledge',		'Acknowledgement (Signed by Patient and Witness)',											6,				6,				0,			'A',		@dbCreator)
				,( @centerID,	4, 'Informed',			'Informed Consent',																			0,				0,				0,			'I',		@dbCreator)
				,( @centerID,	5, 'Surgery',			'(A) Authorization & Surgery Consent (Signed by Patient and Witness)',						6,				6,				1,			'A',		@dbCreator)
				,( @centerID,	6, 'Anesthesia',		'Anesthesia (Signed by Patient and Witness)',												6,				6,				1,			'A',		@dbCreator) 
				,( @centerID,	7, 'Media',				'Media Consent (Signed by Patient)',														4,				4,				0,			'A',		@dbCreator)          
				,( @centerID,	8, 'Waiver',			'Waiver Consent (Signed by Patient and Physician)',											5,				5,				0,			'A',		@dbCreator)          
			)
			AS	temp (CenterID, ID, Name, [Description], [Signatures], [Validations], [Readonly], [Status], CreateBy)
		)
	MERGE INTO	t_EHR_ConsentType AS Target
	USING		ConsentType AS Source
	ON			Target.CenterID = Source.CenterID AND Target.ID = Source.ID
	WHEN		MATCHED AND (Target.Name <> Source.Name OR Target.Description <> Source.Description OR Target.Status <> Source.Status OR Target.Signatures <> Source.Signatures OR Target.Validations <> Source.Validations OR Target.ReadOnly <> Source.ReadOnly) THEN
	UPDATE		SET Target.Name = Source.Name, Target.[Description] = Source.[Description], Target.Status = Source.Status, Target.Signatures = Source.Signatures, Target.Validations = Source.Validations, Target.ReadOnly = Source.ReadOnly
	WHEN		NOT MATCHED BY TARGET THEN
	INSERT		(CenterID, ID, Name, [Description], [Signatures], [Validations], [Readonly], [Status], CreateBy) 
	VALUES		(Source.CenterID, Source.ID, Source.Name, Source.[Description], Source.[Signatures], Source.[Validations], Source.[Readonly], Source.[Status], Source.CreateBy);


