	-- t_EHR_QuestionTemplate -- Module 082 - Patient Education
	WITH EHR_QuestionTemplate (CenterID, QuestionID, ParentQuestionTemplateKey, QuestionVersionNumber, QuestionText, QuestionTypeID, DataScope, Protected, [Required], CreateBy, ModuleDesignID)
	AS (
		SELECT	*
		FROM(VALUES
			 (@centerID, '082A-1',	null, 1,	'Pre-op routine and orientation explained to patient and/or accompanying adult',					3, 'M', 1, 1, @dbCreator, '082')
			,(@centerID, '082A-2',	null, 1,	'Pain assessment scale and pain management plan explained to patient and/or accompanying adult',	3, 'M', 1, 1, @dbCreator, '082')
