	ON			TARGET.QuestionnaireTemplateKey = SOURCE.QuestionnaireTemplateKey
	AND			TARGET.QuestionTemplateKey = SOURCE.QuestionTemplateKey
	WHEN		MATCHED THEN
	UPDATE		SET TARGET.Ordinal = SOURCE.Ordinal, TARGET.[Required] = SOURCE.[Required], TARGET.CreateBy = SOURCE.CreateBy
	WHEN		NOT MATCHED BY TARGET THEN
	INSERT		(QuestionnaireTemplateKey, QuestionTemplateKey, Ordinal, [Required], CreateBy)
	VALUES		(SOURCE.QuestionnaireTemplateKey, SOURCE.QuestionTemplateKey, SOURCE.Ordinal, SOURCE.[Required], SOURCE.CreateBy)
	WHEN		NOT MATCHED BY SOURCE 
	-- do not delete from other centers, only delete ones belonging to this center.
	AND			EXISTS	(	SELECT	* 
							FROM	t_EHR_QuestionnaireTemplate  
							WHERE	QuestionnaireTemplateKey = TARGET.QuestionnaireTemplateKey 
							AND		CenterID = @centerID ) 	
	-- do not delete from  t_EHR_QuestionnaireQuestionTemplate when questionnaire Protected flag is 0 because the non-protected questionnaire is maintained outside initSeed	
	AND			EXISTS	( 	SELECT	* 
							FROM	t_EHR_QuestionnaireTemplate  
							WHERE	QuestionnaireTemplateKey = TARGET.QuestionnaireTemplateKey 
							AND		Protected = 1 ) 
	THEN		DELETE;

