			,(@centerID, 'I', 87, 	'Review Lab-Pathology Results',				'p_EHR_Task_Validation',					'p_EHR_Task_Generation',					@dbCreator)
			,(@centerID, 'I', 2000, 'Lab/Pathology Results Reconciliation',		'p_EHR_Task_Validation_Lab2000', 			'p_EHR_Task_Generation_Lab2000',			@dbCreator)
			,(@centerID, 'I', 3000, 'Triage Documents',							'p_EHR_Task_Validation_TriageDoc', 			'p_EHR_Task_Generation_TriageDoc',			@dbCreator)
			,(@centerID, 'I', 73, 	'Sign Anesthesia Orders',					'p_EHR_Task_Validation_AnesOrd',			'p_EHR_Task_Generation_AnesOrd',			@dbCreator)
			,(@centerID, 'I', 81, 	'Review Supplies Used',						'p_EHR_Task_Validation',					'p_EHR_Task_Generation',					@dbCreator)
			,(@centerID, 'I', 92, 	'Sign Operative Report',					'p_EHR_Task_Validation',					'p_EHR_Task_Generation',					@dbCreator)
			,(@centerID, 'A', 72, 	'Unsigned Physician Orders',				'p_EHR_Task_Validation_PhyOrd', 			'p_EHR_Task_Generation_PhyOrd',				@dbCreator)
			,(@centerID, 'I', 34, 	'Sign Medication Reconciliation',			'p_EHR_Task_Validation',					'p_EHR_Task_Generation',					@dbCreator)
			,(@centerID, 'I', 74, 	'Sign Interim Operative Report',			'p_EHR_Task_Validation',					'p_EHR_Task_Generation',					@dbCreator)
			)
		AS	temp (CenterID, [Status], TaskTemplateID, TaskTemplateName, TaskValidationProcedure, TaskGenerationProcedure, CreateBy)
		)
	MERGE INTO	t_EHR_TaskTemplate AS [Target]
	USING		EHR_TaskTemplate AS [Source]
	ON			[Target].TaskTemplateID = [Source].TaskTemplateID AND [Target].CenterID = [Source].CenterID
	WHEN		MATCHED AND ([Target].[Status] <> [Source].[Status] OR [Target].TaskTemplateName <> [Source].TaskTemplateName OR [Target].TaskGenerationProcedure <> [Source].TaskGenerationProcedure OR [Target].TaskValidationProcedure <> [Source].TaskValidationProcedure) THEN
					UPDATE		SET [Target].TaskTemplateName = [Source].TaskTemplateName, 
									[Target].TaskGenerationProcedure = [Source].TaskGenerationProcedure, 
									[Target].TaskValidationProcedure = [Source].TaskValidationProcedure, 
									[Target].ChangeBy = @dbCreator, 
									[Target].ChangeDate = GETDATE(),
									[Target].[Status] = [Source].[Status]
	WHEN		NOT MATCHED BY TARGET THEN
					INSERT		(	CenterID,      
									[Status],	
									TaskTemplateID,          
									TaskTemplateName,          
									TaskValidationProcedure,          
									TaskGenerationProcedure,          
									CreateBy) 
					VALUES		(	[Source].CenterID, 
									[Source].[Status], 
									[Source].TaskTemplateID, 
									[Source].TaskTemplateName, 
									[Source].TaskValidationProcedure, 
									[Source].TaskGenerationProcedure, 
									[Source].CreateBy);

	/*************************************************
	* Due to as yet unknown reasons, the alias given for a field in a view was not used when constructing a db dictionary record
	*   - the case of the underlying field was used.  Wha???
	* 3/10/2017
	* bug found at new install at Emerald Coast
	**************************************************/
	UPDATE	t_EHR_ModuleTemplate
	SET		[Status] = 'I'
	FROM	t_EHR_ModuleTemplate
	WHERE	ModuleDesignID IN ('072','073')
	AND		CenterID = @CenterID;

	UPDATE	t_EHR_DBDictionary 
	SET		FieldName = 'PhysicianId' 
	WHERE	DBDictionaryKey IN (	SELECT	DBDictionaryKey 
									FROM	t_EHR_DBDictionary	i
									WHERE	i.TableName = 'v_EHR_Header' 
									AND		i.FieldName = 'PhysicianID' COLLATE Latin1_General_CS_AS
									AND		i.CenterID = @CenterID) -- case sensitive.

	/* abandoned live edit trigger procs can be safely removed from dictionary */
	DELETE	t_EHR_TRDictionary 
	WHERE	ProcName NOT IN (	SELECT	name 
								FROM	sys.objects);


	PRINT 'Committing ...'
	COMMIT TRANSACTION;
	PRINT 'Complete ...'

	RETURN;

END TRY
